package com.resource.app.model;import java.util.HashMap;import java.util.Map;import android.text.TextUtils;import com.google.gson.reflect.TypeToken;import com.resource.mark_net.utils.JsonUtils;public class PayResult {	private String resultStatus;	private String result;	private String memo;	private PayResponse payResponse;	public PayResult(Map<String, String> rawResult) {		if (rawResult == null) {			return;		}		for (String key : rawResult.keySet()) {			if (TextUtils.equals(key, "resultStatus")) {				resultStatus = rawResult.get(key);			} else if (TextUtils.equals(key, "result")) {				result = rawResult.get(key);				payResponse = JsonUtils.decode(result,PayResponse.class);			} else if (TextUtils.equals(key, "memo")) {				memo = rawResult.get(key);			}		}	}	@Override	public String toString() {		return "resultStatus={" + resultStatus + "};memo={" + memo				+ "};result={" + result + "}";	}	public String getTradeNo(){		String tradeNo = "";		if (payResponse != null && payResponse.alipay_trade_app_pay_response != null) {			tradeNo = payResponse.alipay_trade_app_pay_response.get("trade_no");		}		return tradeNo;	}	/**	 * @return the resultStatus	 */	public String getResultStatus() {		return resultStatus;	}	/**	 * @return the memo	 */	public String getMemo() {		return memo;	}	/**	 * @return the result	 */	public String getResult() {		return result;	}	class PayResponse {		public HashMap<String, String> alipay_trade_app_pay_response;		public String sign;		public String sign_type;	}}